
import React from 'react';

import ShallowRenderer from 'react-test-renderer/shallow';

import AdaptiveCard from '.';

const renderer = new ShallowRenderer();

describe('Adaptive Card Component', () => {
  const adaptiveCardActivity = {
    object: {
      displayName: 'Hello World!',
      displayNameHTML: {
        __html: 'Hello World!'
      },
      cards: [
        '{"type":"AdaptiveCard","version":"1.0","body":[{"type":"TextBlock","text":"Adaptive Cards","separation":"none"}]}'
      ],
      objectType: 'comment'
    },
    verb: 'post'
  };

  it('renders properly', () => {
    renderer.render(
      <AdaptiveCard cards={adaptiveCardActivity.object.cards} displayName={adaptiveCardActivity.object.displayName} />
    );
    const component = renderer.getRenderOutput();

    expect(component).toMatchSnapshot();
  });
});
