import {WIDGET_ADAPTIVE_CARD, WIDGET_ADAPTIVE_CARD_OFF, WIDGET_ADAPTIVE_CARD_ON} from './constants';
// eslint-disable-next-line import/prefer-default-export
export function getAdaptiveCardFeatureState(features) {
  let adaptiveCardFeatureState = WIDGET_ADAPTIVE_CARD_OFF;

  if (features !== undefined && features !== null) {
    const items = features.get('items');

    if (features && items && items.size > 0) {
      const adaptiveCardFeatures = items.get(WIDGET_ADAPTIVE_CARD);

      if (adaptiveCardFeatures) {
        adaptiveCardFeatureState = WIDGET_ADAPTIVE_CARD_ON;
      }
    }
  }

  return adaptiveCardFeatureState;
}
